import axios from 'axios';
import { useState, useEffect } from 'react';
import './App.css';

function App() {
  let offset = 0;
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false)

  const loadMovies = () => {
    setLoading(true);
    axios.get(`http://localhost:3001/movies?limit=4&page=${offset}`)
      .then((res) => {
        const newMovies = [];
        //setLoading(false);
        //console.log(res.data)
        //setMovies(res.data)

        //if (page <= 5) {
        res.data.forEach((item, index) => newMovies.push(item));
        console.log(newMovies)
        setMovies((current) => [...current, ...newMovies]);
        //}
        //if (page > 6) {
        //console.log('you reach the end.')
        //}

      })
      .catch((err) => console.log(err))
    offset += 1;
    console.log('offset: ' + offset)
  }

  const handleScroll = (e) => {
    if (window.innerHeight + e.target.documentElement.scrollTop + 1 >= e.target.documentElement.scrollHeight) {
      loadMovies();
    }
  }

  useEffect(() => {
    loadMovies();
    window.addEventListener('scroll', handleScroll)
  }, [])


  return (

    <div>
      <h1>Movies</h1>
      <div>
        {movies.map((e, i) => {
          return <div key={i} className='box border'><div style={{position: 'absolute', left: '0', top: '0'}}><p>{e.title}</p></div></div>; 
        })}
        <div style={{padding:'20px 20px', border:'1px solid #000'}}>{loading && (<p>Loading..</p>)}</div>
      </div>
      {/*<div>
        {movies.map((e, i) => {
          return <div key={i} style={{width:'300px', height:'100px', border:'1px solid #000'}}>{e.title}<br/></div>;
        })}
      </div><div style={{padding:'20px 20px', border:'1px solid #000'}}>{loading && (<p>Loading..</p>)}</div>*/}
    </div>
  );
}

export default App;
