import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import styles from '../css/register.module.scss';
import styled from '../css/content.module.css';

function App() {
    let offset = 0;
    const [trending, setTrending] = useState([]);
    const [loadingTrend, setLoadingTrend] = useState(false);
    const navigate = useNavigate();

    const logout = async () => {
        await axios.get('http://localhost:3001/logout')
            .then((res) => {
                console.log(res.data.msg)
                navigate('/login')

                sessionStorage.removeItem('Auth Token');
                sessionStorage.removeItem('Username')
            }).catch((err) => {
                console.log(err)
            })
    }

    const loadTrending = () => {
        setLoadingTrend(true);
        axios.get(`https://pokeapi.co/api/v2/pokemon?limit=4&offset=${offset}`)
        .then((res) => {
            setLoadingTrend(false);
            var newTrending = [];

            if (offset <= 12) {
                res.data.results.forEach((t)=> newTrending.push(t.name, t.url));
                console.log(newTrending)
                setTrending((current) => [...current, ...newTrending]);
            }
            if (offset > 13) {
                console.log('you reach the end')
            }
        })
        .catch((err) => { console.log(err) })
        offset += 4;
        console.log('offset: '+offset)
    }

    const handleScrollXTrending = () => {
        const Div = document.getElementById('trending_scrollable')
        var container = document.getElementById('trending_container')
        var px = (75 * (container.clientWidth)) / 100;
        Div.scrollLeft += px;
        loadTrending();
    }

    const handleScrollInvertXTrending = () => {
        const Div = document.getElementById('trending_scrollable')
        var container = document.getElementById('trending_container')
        var px = (75 * (container.clientWidth)) / 100;
        Div.scrollLeft -= px;
    }


    useEffect(() => {
        let authToken = sessionStorage.getItem('Auth Token')
        /*
        if (authToken) {
            navigate('/home')
        }
        if (!authToken) {
            navigate('/login')
        }
        */
        loadTrending();

        var trending_scrollable = document.getElementById('trending_scrollable')
        const scrollXtrending = document.getElementById('scrollXtrending')
        const scrollInvertXtrending = document.getElementById('scrollInvertXtrending')

        scrollXtrending.addEventListener('click', handleScrollXTrending)
        scrollInvertXtrending.addEventListener('click', handleScrollInvertXTrending)
        trending_scrollable.addEventListener('touchend', loadTrending)
    }, [])

    const sessionUsername = sessionStorage.Username;

    return (
        <div>
            <header className={styles.header}>
                <section name='top-nav'>
                    <button style={{width: '20%'}}>index</button>
                    <button style={{width: '40%'}}>search</button>
                    <button style={{width: '15%'}}>movies</button>
                    <button style={{width: '15%'}}>shows</button>
                    <button style={{width: '10%'}}>menu</button>
                </section>
            </header>

            <main>

                {/*<h1>Welcome Home</h1>
                <h3>{sessionUsername}</h3>
                <button type="button" onClick={logout}>Log Out</button>*/}

                <section name='hilight-poster'>
                    <div style={{height: '30vw', border: '1px solid #000'}}>hilight</div>
                    <br/>
                </section>

                <section>
                    <article style={{border: '1px solid #000'}}>
                        <h2>Trending</h2>
                        <div id='trending_container' className={styled['container-trending']}>
                            <div id='trending_scrollable' className={styled['scrollable-trending']}>
                                <div className={styled['content-wrap']}>
                                    <div name='box-top' className={styled['combine-trending']}>
                                        {trending.map((e, i) => {
                                            if (i % 2 === 0) {
                                                return <div key={i} className={styled.box}>{e}{loadingTrend && (<p>Loading..</p>)}</div>;
                                            }
                                        })}
                                    </div>
                                    <div name='box-bottom' style={{ border: '1px solid blue'}}>
                                        {trending.map((e, i) => {
                                            if (i % 2 === 1 ) {
                                                return <div key={i} className={styled.box}><img src={e}/></div>;
                                            }
                                        })}
                                    </div>
                                </div>
                            </div>

                            <div>
                                <button type='button' id='scrollXtrending'>+X</button>
                                <button type='button' id='scrollInvertXtrending'>-X</button>
                            </div>
                        </div>
                    </article>

                    <article style={{border: '1px solid #000'}}>
                        <h2>Most Watching</h2>

                        <div style={{whiteSpace:'nowrap', overflow:'auto', background:'#ddd'}}>
                            <div style={{width:'200px', height: '100px', display: 'inline-block', border: '1px solid #000'}}>1</div>
                            <div style={{width:'200px', height: '100px', display: 'inline-block', border: '1px solid #000'}}>2</div>
                            <div style={{width:'200px', height: '100px', display: 'inline-block', border: '1px solid #000'}}>3</div>
                            <div style={{width:'200px', height: '100px', display: 'inline-block', border: '1px solid #000'}}>4</div>
                            <div style={{width:'200px', height: '100px', display: 'inline-block', border: '1px solid #000'}}>5</div>
                            <div style={{width:'200px', height: '100px', display: 'inline-block', border: '1px solid #000'}}>6</div>
                        </div>
                    </article>

                    <article style={{border: '1px solid #000'}}>
                        <h2>Catagories</h2>

                        <div style={{whiteSpace:'nowrap', overflow:'auto', background:'#ddd'}}>
                            <div style={{width:'150px', height: '100px', display: 'inline-block', border: '1px solid #000'}}>Movies</div>
                            <div style={{width:'150px', height: '100px', display: 'inline-block', border: '1px solid #000'}}>Shows</div>
                            <div style={{width:'150px', height: '100px', display: 'inline-block', border: '1px solid #000'}}>Action</div>
                            <div style={{width:'150px', height: '100px', display: 'inline-block', border: '1px solid #000'}}>Crime</div>
                            <div style={{width:'150px', height: '100px', display: 'inline-block', border: '1px solid #000'}}>Romance</div>
                            <div style={{width:'150px', height: '100px', display: 'inline-block', border: '1px solid #000'}}>Comedy</div>
                        </div>
                    </article>

                    <br/>
                </section>
            </main>




            <footer style={{ position: 'relative', border: '1px solid #000' }}>
                <ul>Footer
                    <li><a href="#">Privacy Policy</a></li>
                    <li><a href="#">Terms of Use</a></li>
                    <li><a href="#">Do Not Sell My Personal Information</a></li>
                    <li><a href="#">© 2022 TVnow</a></li>
                </ul>

            </footer>
        </div>
    );
}

export default App;
