import React from 'react';
import logo from './logo.svg';
//import './css/App.css';
import './css/alt.css';

class Square extends React.Component {
  render() {
    return <button type="button" className="blck border" onClick={() => this.props.onClick()}>
      {this.props.value}
    </button>
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill(null),
      xIsNext: true
    }
  }

  handleClick(i){
    const squares = this.state.squares.slice();
    squares[i] = this.state.xIsNext? 'X':'O';
    this.setState({
      squares: squares,
      xIsNext: !this.state.xIsNext
    });
  }

  renderSquare(i){
    return <Square value={this.state.squares[i]} onClick={() => this.handleClick(i)}/>
  }
  render() {
    const status = 'Next Player: ' + (this.state.xIsNext? 'X': 'O');
    return(
      <div>
        <p>{status}</p>
        <div>
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div>
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div>
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i=0; i<lines.length; i++) {
    const [a,b,c] = lines[i];
    if(squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}

export default App;
