import React from 'react';
import logo from './logo.svg';
//import './css/App.css';
import './css/alt.css';

class Square extends React.Component {
  render() {
    return <button type="button" className="blck border" onClick={() => this.props.onClick()}>
      {this.props.value}
    </button>
  }
}

class App extends React.Component {

  renderSquare(i) {
    return <Square value={this.props.squares[i]} onClick={() => this.props.onClick(i)} />
  }
  render() {

    return (
      <div>
        <div>
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div>
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div>
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      history: [{
        squares: Array(9).fill(null)
      }],
      xIsNext: true
    }
  }

  handleClick(i) {
      const history = this.state.history;    
      const current = history[history.length - 1];    
      const squares = current.squares.slice();
    if (calculateWinner(squares || squares[i])) {
      return;
    }
    squares[i] = this.state.xIsNext ? 'X' : 'O';
    this.setState({
      history: history.concat([{
        squares: squares
      }]),
      xIsNext: !this.state.xIsNext
    });
  }

  render() {
    const history = this.state.history; 
    const current = history[history.length - 1]; 
    const winner = calculateWinner(current.squares); 
    let status; 
    if (winner) { 
      status = 'Winner: ' + winner; 
    } else { 
      status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O'); 
    }
    return (
      <div>
        <App squares={current.squares} onClick={(i) => this.handleClick(i)}/>
        <p>{status}</p>
        <p></p>
      </div>
    );
  }
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}

export default Game;
