import axios from 'axios';
import { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [array, setArray] = useState([]);
  let offset = 0;

  const handleClick = () => {
    axios.get(`https://pokeapi.co/api/v2/pokemon?limit=10&offset=${offset}`)
      .then((res) => {
        const newArray = [];
        res.data.results.forEach((p) => newArray.push(p.name));


        let test = newArray.slice();
        console.log(test)
        //setArray(array => [...array, 'india'])
        setArray(test)
      })
      .catch((err) => { console.log(err) })
    offset += 10;
  }


  return (
    <div>
      <p></p>
      <button type='button' onClick={handleClick}>Click</button>
        {array.map((p, i) => {
            return <p key={i}>{p}</p>
            //return <div key={i} style={{width:'50px', height:'50px', border:'1px solid #000'}}>{p}</div>;
          })}
    </div>
  );
}

export default App;
