import './css/alt.css';

import { useState } from 'react';



function App() {
  const [number, setNumber] = useState('');

  const add_hyphen = (e) => {
    if ((e.target.value.length === 3) || (e.target.value.length === 7) ) {
      e.target.value += '-'
    }

  }

  const sendData = () => {
    let int = number
    int.replace(/0/g, "'");    
    console.log(int);
  }

  return (
    <div>
      <form>
        <input type='tel' onChange={(e) => {setNumber(e.target.value); add_hyphen(e)}} placeholder=" 089-XXX-XXXX" pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}" maxLength={12}/>
        <input type="tel" placeholder=" 089-XXX-XXXX" pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}" maxLength={10}/>
      </form>

      <button type='button' onClick={sendData}>Send Data</button>

      <p>{number}</p>
    </div>
  );
}

export default App;
