import React, { useState } from "react";

function App() {

  return (
    <>
      <MyComp/>
    </>
  );
};

function MyComp() {
  const [isActiveP, setActiveP] = useState(false);
  const toggleP =() => {
    setActiveP(!isActiveP);
  }

  return(
    <>
    <button type='button' onClick={toggleP}>Show P</button>
         {isActiveP && (<p type="button">Hey!</p>)}
    </>
  );
}

export default App;
