import axios from 'axios';
import { useState, useEffect } from 'react';
import './App.css';

function App() {
  let offset = 0;
  const [pokemon, setPokemon] = useState([]);
  const [loading, setLoading] = useState(false);
  const [output, setOutput] = useState([]);

  const loadPokemon = () => {
    setLoading(true);
    axios.get(`https://pokeapi.co/api/v2/pokemon?limit=4&offset=${offset}`)
      .then((res) => {
        var newPokemon = [];
        var newPokemonImg = [];
        setLoading(false);

        if (offset > 13) {
          console.log('you reach the end.')
        }
        if (offset <= 12) {
          res.data.results.forEach((p) => newPokemon.push(p.name, p.url));
          console.log(newPokemon)
          setPokemon((current) => [...current, ...newPokemon]);


        }
      })
      .catch((err) => console.log(err))
    offset += 4;
    console.log('offset: '+offset)

  }


  const handleScrollX = (e) => {
    const Div = document.getElementById('scrollDemo');
    var container = document.getElementById('container');
    var px = (75 * (container.clientWidth)) / 100;
    Div.scrollLeft += px;
    loadPokemon();
    console.log('position: ' + Div.scrollLeft)
  }
  const handleScrollInvertX = (e) => {
    const Div = document.getElementById('scrollDemo');
    var container = document.getElementById('container');
    var px = (75 * (container.clientWidth)) / 100;
    Div.scrollLeft -= px;
    console.log('position: ' + Div.scrollLeft)
  }


  const getScrollPos = (e) => {
    loadPokemon();
    //let Div = document.getElementById('scrollDemo');
    //console.log('scroll ' + e.target.scrollLeft)
    /*
    if ( (Div.scrollLeft > ((Div.clientWidth/3)*1)) && (Div.scrollLeft < ((Div.clientWidth/2.99)*1))) {
      loadPokemon();
      console.log('1: ' + Div.scrollLeft)
    }
    if ( (Div.scrollLeft > ((Div.clientWidth/3)*2)) && (Div.scrollLeft < ((Div.clientWidth/2.99)*2))) {
      loadPokemon();
      console.log('2: ' + Div.scrollLeft)
    }
    */

  }

  /*
  const scrollX = () => {
    document.getElementById('scrollDemo').scrollLeft += 50;
  }*/


  useEffect(() => {
    loadPokemon();
    var scrollX, scrollInvertX;
    var scrollDemo = document.getElementById('scrollDemo')
    scrollX = document.getElementById('scrollX')
    scrollInvertX = document.getElementById('scrollInvertX')

    scrollX.addEventListener('click', handleScrollX)
    scrollInvertX.addEventListener('click', handleScrollInvertX)
    scrollDemo.addEventListener('touchend', loadPokemon)
    //window.addEventListener('scroll', getScrollPos)

  }, [])


  return (

    <div id="container">

      <div id="scrollDemo" onScroll={getScrollPos}>
        <div className='container'>
          <div name='box_top' style={{ border: '1px solid red' }}>
            {pokemon.map((e, i) => {
              if (i % 2 === 0) {
                return <div key={i} className='box'><p>{e}</p>{loading && (<p>Loading..</p>)}</div>
              }
            })}
          </div>
          <div name='box_bottom' style={{ border: '1px solid blue' }}>
            {pokemon.map((e, i) => {
              if (i % 2 === 1) {
                return <div key={i} className='box'><img src={e} /></div>
              }

            })}
          </div>
          {/*<p>{pokemon}</p>*/}
          {/*{pokemon.map((element, index) => {

            return <div key={index} className='box'><p>{element}</p>{loading && (<p>Loading..</p>)}</div>
          })}*/}


        </div>
      </div>

      <div>
        <button type="button" id="scrollX">Scroll X</button>
        <button type="button" id="scrollInvertX">Scroll invertX</button>
      </div>

    </div>
  );
}

export default App;
