import axios from 'axios';
import { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [array, setArray] = useState(['a', 'b']);
  let offset = 0;

  const handleClick = () => {
    axios.get(`https://pokeapi.co/api/v2/pokemon?limit=10&offset=${offset}`)
    .then((res) => {
      var newPokemon = [];
      res.data.results.forEach((p) => newPokemon.push(p.name));
      //console.log(newPokemon)
      let newPokemonCopy = newPokemon
      setArray((current) => [...current, ...newPokemon, '+++']);
      console.log(newPokemon + ': ' + newPokemonCopy)

    })
    .catch((err) => console.log(err))
  offset += 10;
  }

  useEffect(()=>{
    handleClick();
    var test = document.getElementById('test');
    test.addEventListener('click', handleClick)  
  },[])


  return (
    <div>
      <p></p>
      <button type='button' id='test'>Click</button>
        {array.map((element, index) => {
            return <p key={index}>{element}</p>
            //return <div key={i} style={{width:'50px', height:'50px', border:'1px solid #000'}}>{p}</div>;
          })}
    </div>
  );
}

export default App;
