import './css/alt.css';

import { useEffect } from 'react';



function App() {

  const add_hyphen = (e) => {
    if ((e.target.value.length === 3) || (e.target.value.length === 7) ) {
      e.target.value += '-'
    }

  }

  return (
    <div>
      <form>
        <input type='tel' onChange={add_hyphen}/>
        <input type="tel" placeholder=" 089-XXX-XXXX" pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}" maxLength={10}/>
      </form>

    </div>
  );
}

export default App;
