import axios from 'axios';
import { useState, useEffect } from 'react';
import './App.css';

function App() {
  let page = 0;
  const [movies, setMovies] = useState([]);

  const loadMovies = () => {
    //setLoading(true);
    axios.get(`http://localhost:3001/movies?limit=10&page=${page}`)
      .then((res) => {
        var newMovies = [];
        //setLoading(false);
        console.log(res.data)
        setMovies(res.data)

        if (page <= 25) {
          res.data.forEach((item, index) => newMovies.push(item));
          //console.log(newMovies)
          setMovies((current) => [...current, ...newMovies]);
        }
        if (page > 26) {
          console.log('you reach the end.')
        }

      })
      .catch((err) => console.log(err))
    page += 1;
    console.log('page: ' + page)
  }

  const handleScroll = (e) => {
    if (window.innerHeight + e.target.documentElement.scrollTop + 1 >= e.target.documentElement.scrollHeight) {
      loadMovies();
    }
  }

  useEffect(() => {
    loadMovies();
    window.addEventListener('scroll', handleScroll)
  }, [])


  return (

    <div>
      <h1>Movies</h1>
      <div>
        {movies.map((e, i) => {
          return <div key={i} style={{width:'300px', height:'300px', border:'1px solid #000'}}>{e.title}</div>;
        })}
      </div>
    </div>
  );
}

export default App;
