import React, { useEffect, useState } from "react";
import jwt_decode from 'jwt-decode';
import { BrowserRouter as Router, Routes, Route, useNavigate, Navigate } from 'react-router-dom';


function Login() {
    const ggClientId = '412920764604-j8act2t26db6ci1jq74bdj3fd7ju64bd.apps.googleusercontent.com';
    const [user, setUser] = useState({});
    let navigate = useNavigate();

    function handleCallbackGGResponse(response) {
        console.log('id token:' + response.credential);
        console.log('id refresh:' + response.refreshToken);
        var userObject = jwt_decode(response.credential);
        sessionStorage.setItem('Google Token', response.refreshToken);
        console.log(userObject);
        setUser(userObject);
    }

    function handleSignOut(event) {
        setUser({});
        navigate('/login');
        document.getElementById('signInDiv').hidden = false;
    }

    useEffect(() => {
        /* global google */
        google.accounts.id.initialize({
            client_id: ggClientId,
            callback: handleCallbackGGResponse

        });

    }, []);

    return (
        <div>
            <div id="g_id_onload"
                data-client_id="412920764604-j8act2t26db6ci1jq74bdj3fd7ju64bd.apps.googleusercontent.com"
                data-context="signin"
                data-ux_mode="popup"
                data-login_uri="https://localhost:3000"
                data-nonce=""
                data-auto_prompt="false">
            </div>

            <div class="g_id_signin"
                data-type="standard"
                data-shape="rectangular"
                data-theme="filled_blue"
                data-text="signin_with"
                data-size="large"
                data-logo_alignment="left">
            </div>

        </div>
    );
}

export default Login;
