import React, { Component } from 'react';
import PropTypes from 'prop-types';
import logo from './logo.svg';
import ScrollMenu from 'react-horizontal-scrolling-menu';
import './App.css';

let list = [
  { name: 'item1' },
  { name: 'item2______________' },
  { name: 'item3' },
  { name: 'item4' },
  { name: 'item5' },
  { name: 'item6' },
  { name: 'item7______________' },
  { name: 'item8' },
  { name: 'item9' },
  { name: 'item10' },
  { name: 'item11' },
  { name: 'item12' },
  { name: 'item13' },
  { name: 'item14______________' },
  { name: 'item15' },
  { name: 'item16' },
  { name: 'item17' },
  { name: 'item18' },
  { name: 'item19' },
  { name: 'item20' },
  { name: 'item21' },
  { name: 'item22______________' },
  { name: 'item23' },
  { name: 'item24' },
  { name: 'item25' },
];

// eslint-disable-next-line react/prop-types
const MenuItem = ({ text, selected }) => {
  return <div className={`menu-item ${selected ? 'active' : ''}`}>{text}</div>;
};

export const Menu = (list, selected) =>
  list.map(el => {
    const { name } = el;
    const onClick = () => console.log('original onClick ', name);
    return (
      <MenuItem text={name} key={name} selected={selected} onClick={onClick} />
    );
  });

const Arrow = ({ text, className }) => {
  return <div className={className}>{text}</div>;
};
Arrow.propTypes = {
  text: PropTypes.string,
  className: PropTypes.string,
};

export const ArrowLeft = Arrow({ text: '<', className: 'arrow-prev' });
export const ArrowRight = Arrow({ text: '>', className: 'arrow-next' });

class App extends Component {
  state = {
    alignCenter: true,
    clickWhenDrag: false,
    dragging: true,
    hideArrows: true,
    hideSingleArrow: true,
    itemsCount: list.length,
    selected: 'item1',
    scrollToSelected: false,
    translate: undefined,
    transition: 0.4,
    wheel: true,
    showList: true,
    inertiascrolling: false,
    slowdownFactor: 0.25,
  };

  constructor(props) {
    super(props);
    this.menu = null;
    this.menuItems = Menu(list.slice(0, list.length), this.state.selected);
  }

  componentDidUpdate(prevProps, prevState) {
    const { alignCenter } = prevState;
    const { alignCenter: alignCenterNew } = this.state;
    if (alignCenter !== alignCenterNew && this.menu) {
      this.menu.setInitial();
      this.menu.forceUpdate();
      this.forceUpdate();
    }
  }

  onUpdate = ({ translate }) => {
    console.log(`onUpdate: translate: ${translate}`);
    this.setState({ translate });
  };

  onFirstItemVisible = () => {
    console.log('first item is visible');
  };

  onLastItemVisible = () => {
    console.log('last item is visible');

    const newItems = Array(5)
      .fill(1)
      .map((el, ind) => ({ name: `item${list.length + ind + 1}` }));
    list = list.concat(newItems)
    this.menuItems = Menu(
      list,
      list.slice(-1)[0].name,
    );
    this.setState({
      itemsCount: list.length,
      selected: this.state.selected,
    });
  };

  onSelect = key => {
    console.log(`onSelect: ${key}`);
    this.setState({ selected: key });
  };

  setItemsCount = ev => {
    const { itemsCount = list.length, selected } = this.state;
    const val = +ev.target.value;
    const itemsCountNew =
      !isNaN(val) && val <= list.length && val >= 0
        ? +ev.target.value
        : list.length;
    const itemsCountChanged = itemsCount !== itemsCountNew;

    if (itemsCountChanged) {
      this.menuItems = Menu(list.slice(0, itemsCountNew), selected);
      this.setState({
        itemsCount: itemsCountNew,
      });
    }
  };

  setSlowdownFactor = ev => {
    this.setState({ slowdownFactor: ev.target.value });
  };

  setSelected = ev => {
    const { value } = ev.target;
    this.setState({ selected: String(value) });
  };

  toggle = () => {
    this.setState({ showList: !this.state.showList });
  };

  render() {
    const {
      alignCenter,
      clickWhenDrag,
      hideArrows,
      dragging,
      hideSingleArrow,
      itemsCount,
      selected,
      translate,
      transition,
      wheel,
      showList,
      scrollToSelected,
      inertiascrolling,
      slowdownFactor,
    } = this.state;

    const menu = this.menuItems;

    const checkboxStyle = {
      margin: '5px 10px',
    };
    const valueStyle = {
      margin: '5px 10px',
      display: 'inline-block',
    };

    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title">React horizontal scrolling menu</h1>
        </header>
        <p className="App-intro">
          Horizontal scrolling menu example. Click arrow or drag items.
        </p>

        <button onClick={this.toggle}>Toggle Show/hide</button>

        {showList && (
          <ScrollMenu
            alignCenter={alignCenter}
            arrowLeft={ArrowLeft}
            arrowRight={ArrowRight}
            clickWhenDrag={clickWhenDrag}
            data={menu}
            dragging={dragging}
            hideArrows={hideArrows}
            hideSingleArrow={hideSingleArrow}
            inertiaScrolling={inertiascrolling}
            inertiaScrollingSlowdown={slowdownFactor}
            onFirstItemVisible={this.onFirstItemVisible}
            onLastItemVisible={this.onLastItemVisible}
            onSelect={this.onSelect}
            onUpdate={this.onUpdate}
            ref={el => (this.menu = el)}
            rtl={false}
            scrollBy={0}
            scrollToSelected={scrollToSelected}
            selected={selected}
            transition={+transition}
            translate={translate}
            useButtonRole={true}
            wheel={wheel}
          />
        )}

        <form className="properties">
          <label style={checkboxStyle}>
            Align center
            <input
              name="alignCenter"
              type="checkbox"
              checked={alignCenter}
              onChange={() => this.setState({ alignCenter: !alignCenter })}
            />
          </label>
          <label style={checkboxStyle}>
            Dragging
            <input
              name="dragging"
              type="checkbox"
              checked={dragging}
              onChange={() => this.setState({ dragging: !dragging })}
            />
          </label>
          <label style={checkboxStyle}>
            Click when drag end
            <input
              name="clickWhenDrag"
              type="checkbox"
              checked={clickWhenDrag}
              onChange={() => this.setState({ clickWhenDrag: !clickWhenDrag })}
            />
          </label>
          <label style={checkboxStyle}>
            Scroll to selected item
            <input
              name="scrollToSelected"
              type="checkbox"
              checked={scrollToSelected}
              onChange={() =>
                this.setState({ scrollToSelected: !scrollToSelected })
              }
            />
          </label>
          <label style={checkboxStyle}>
            Use mouse wheel
            <input
              name="wheel"
              type="checkbox"
              checked={wheel}
              onChange={() => this.setState({ wheel: !wheel })}
            />
          </label>
          <label style={checkboxStyle}>
            Hide arrows if items width less than menu width
            <input
              name="hideArrows"
              type="checkbox"
              checked={hideArrows}
              onChange={() => this.setState({ hideArrows: !hideArrows })}
            />
          </label>
          <label style={checkboxStyle}>
            Hide left/right arrows if first/last item visible
            <input
              name="hideSingleArrow"
              type="checkbox"
              checked={hideSingleArrow}
              onChange={() =>
                this.setState({ hideSingleArrow: !hideSingleArrow })
              }
            />
          </label>
          <br />
          <div style={valueStyle}>Translate: {(translate || 0).toFixed(2)}</div>
          <label style={valueStyle}>
            Selected:
            <input
              style={{ margin: '0 5px' }}
              name="selected"
              type="text"
              value={selected}
              onChange={this.setSelected}
            />
          </label>
          <label style={valueStyle}>
            Transition duration:
            <input
              style={{ margin: '0 5px' }}
              name="transition"
              type="number"
              value={transition || 0}
              min={0}
              max={10}
              onChange={ev =>
                this.setState({
                  transition: !isNaN(+ev.target.value) ? +ev.target.value : 0,
                })
              }
            />
          </label>
          <label style={valueStyle}>
            Items count:
            <input
              style={{ margin: '0 5px' }}
              name="itemsCount"
              type="number"
              value={itemsCount}
              min={0}
              max={list.length}
              onChange={this.setItemsCount}
            />
          </label>
          <label style={checkboxStyle}>
            Inertia Scrolling
            <input
              name="inertiascrolling"
              type="checkbox"
              checked={inertiascrolling}
              onChange={() =>
                this.setState({ inertiascrolling: !inertiascrolling })
              }
            />
          </label>
          <label style={valueStyle}>
            Inertia scrolling slowdown:
            <input
              style={{ margin: '0 5px' }}
              name="slowdownFactor"
              type="number"
              value={slowdownFactor}
              onChange={this.setSlowdownFactor}
            />
          </label>
        </form>
        <hr />
        <div>
          <a
            /* eslint-disable react/jsx-no-target-blank */
            target="_blank"
            rel="noopener"
            href="https://github.com/asmyshlyaev177/react-horizontal-scrolling-menu"
          >
            Project on GitHub
          </a>
        </div>
      </div>
    );
  }
}

export default App;
