import axios from 'axios';
import { useState, useEffect } from 'react';
import './App.css';

function App() {
  let offset = 0;
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false)

  const loadMovies = () => {
    setLoading(true);
    axios.get(`http://localhost:3001/movies?limit=10&page=${offset}`)
      .then((res) => {
        const newMovies = [];
        //setLoading(false);
        //console.log(res.data)
        //setMovies(res.data)

        //if (page <= 5) {
          res.data.forEach((item) => newMovies.push(item.title));
          //console.log(newMovies)
          setMovies((current) => [...current, ...newMovies]);
        //}
        //if (page > 6) {
          //console.log('you reach the end.')
        //}

      })
      .catch((err) => console.log(err))
    offset += 1;
    console.log('offset: ' + offset)
  }

  const handleScroll = (e) => {
    if (window.innerHeight + e.target.documentElement.scrollTop + 1 >= e.target.documentElement.scrollHeight) {
      loadMovies();
    }
  }

  useEffect(() => {
    loadMovies();
    window.addEventListener('scroll', handleScroll)
  }, [])


  return (

    <div>
      <h1>Movies</h1>
      <div>
        {movies.map((e, i) => {
          return <div key={i} style={{width:'300px', height:'100px', border:'1px solid #000'}}>{e}{loading && (<p>Loading..</p>)}<br/></div>;
        })}
      </div>
    </div>
  );
}

export default App;
