import './css/alt.css';
import Head from 'next/head';
import { useEffect, useState } from 'react';
import axios from 'axios';
import Div from './components/Div';



function App() {
  let offset = 0;
  const [pokemon, setPokemon] = useState([])

  const loadMorePokemon = () => {
    axios.get(`https://pokeapi.co/api/v2/pokemon?limit=10&offset=${offset}`)
      .then((res) => {

        const newPokemon = [];
        res.data.results.forEach((p) => newPokemon.push(p.name));
        setPokemon((oldPokemon) => [...oldPokemon, ...newPokemon])
      })
      .catch((err) => { console.log(err) })
    offset += 10;
  }

  const handleScroll = (e) => {
    console.log(e.target.documentElement.scrollTop)
    if (window.innerHeight + e.target.documentElement.scrollTop + 1 >= e.target.documentElement.scrollHeight) {
      loadMorePokemon();
    }
  }

  useEffect(() => {
    loadMorePokemon();
    window.addEventListener('scroll', handleScroll)
  }, [])

  return (
    <div>
      <Head>
        <title>infinite scroll</title>
      </Head>

      <main>
        <div>
          <br/>
        </div>
        <div style={{whiteSpace:'nowrap', overflow:'auto', background:'#ddd'}}>
          <img src="https://cdn-icons-png.flaticon.com/128/3496/3496528.png" style={{width:'300px', display:'inline-block'}} />
          <img src="https://cdn-icons-png.flaticon.com/128/135/135763.png" style={{width:'300px', display:'inline-block'}} />
          <img src="https://cdn-icons-png.flaticon.com/128/2515/2515249.png" style={{width:'300px', display:'inline-block'}} />
          <img src="https://cdn-icons-png.flaticon.com/128/1744/1744944.png" style={{width:'300px', display:'inline-block'}} />
        </div>
        <div>
          {pokemon.map((p, i) => {
            return <Div key={i} pokename={p}></Div>
            //return <div key={i} style={{width:'50px', height:'50px', border:'1px solid #000'}}>{p}</div>;
          })}
        </div>
        <p>Loading..</p>
      </main>
    </div>
  );
}

export default App;
