import axios from 'axios';
import { useState, useEffect } from 'react';
import './App.css';

function App() {
  let offset = 0;
  const [pokemon, setPokemon] = useState([]);
  const [pokemonImg, setPokemonImg] = useState([]);
  const [loading, setLoading] = useState(false);
  const [output, setOutput] = useState([]);

  const loadPokemon = () => {
    setLoading(true);
    axios.get(`https://pokeapi.co/api/v2/pokemon?limit=4&offset=${offset}`)
      .then((res) => {
        var newPokemon = [];
        var newPokemonImg = [];
        setLoading(false);

        if (offset <= 12) {
          res.data.results.forEach((p) => newPokemon.push(p.name, p.url));
          res.data.results.forEach((p) => newPokemonImg.push(p.url));
          console.log(newPokemon)
          setPokemon((current) => [...current, ...newPokemon]);
          setPokemonImg((current) => [...current, ...newPokemonImg]);

        }
        if (offset > 12) {
          console.log('you reach the end.')

        }

      })
      .catch((err) => console.log(err))
    offset += 4;

  }


  const handleScrollX = (e) => {
    const Div = document.getElementById('scrollDemo');
    var container = document.getElementById('container');
    var px = (75 * (container.clientWidth)) / 100;
    Div.scrollLeft += px;
    loadPokemon();
    console.log('position: ' + Div.scrollLeft)
  }
  const handleScrollInvertX = (e) => {
    const Div = document.getElementById('scrollDemo');
    var container = document.getElementById('container');
    var px = (75 * (container.clientWidth)) / 100;
    Div.scrollLeft -= px;
    console.log('position: ' + Div.scrollLeft)
  }


  const getScrollPos = (e) => {
    //let Div = document.getElementById('scrollDemo');
    //console.log('scroll ' + e.target.scrollLeft)
    if (e.target.scrollLeft >= 500) {
      //console.log('congrat! you reach 500')

    }
  }

  //test
  let test = pokemon;
  console.log(test)


  useEffect(() => {
    loadPokemon();
    var scrollX, scrollInvertX;

    scrollX = document.getElementById('scrollX')
    scrollInvertX = document.getElementById('scrollInvertX')

    scrollX.addEventListener('click', handleScrollX)
    scrollInvertX.addEventListener('click', handleScrollInvertX)

  }, [])

  /*
  const scrollX = () => {
    document.getElementById('scrollDemo').scrollLeft += 50;
  }*/

  //test get odd even
  var arr = [];
  var odd = [];
  var even = [];

  for (var i = 0; i < pokemon.length; i++) {
    let findOdd = () => {
      if (i % 2 === 0) {
        odd.push(pokemon[i])
        //console.log(odd.push(pokemon[i]))
      }
    }
    findOdd();
    let findEven = () => {
      if (i % 2 === 1) {
        even.push(pokemon[i])
      }

    }
    findEven();
    //console.log('odd: '+odd)
  }

  var finalValue = {
    'name': odd,
    'img': even
  }

  console.log('odd: ' + odd)
  console.log('even: ' + even)
  console.log(finalValue.name)

  var valueI = ['A', 'B', 'C'], valueII = ['a', 'b', 'c'];

  var arr = {
    'big': valueI,
    'small': valueII
  }




  return (
    <div id="container">

      <div id="scrollDemo" onScroll={getScrollPos}>
        <div className='container'>
          {odd.map((element, index) => {
            return <div key={index} className='box'><p>{element}</p>{loading && (<p>Loading..</p>)}</div>
          })}
          {/*<p>{pokemon}</p>*/}
          {/*{pokemon.map((element, index) => {

            return <div key={index%2 === 0} className='box'><p>{element}</p><img src={even}/>{loading && (<p>Loading..</p>)}</div>
          })}*/}


        </div>
      </div>

      <div>
        <button type="button" id="scrollX">Scroll X</button>
        <button type="button" id="scrollInvertX">Scroll invertX</button>
      </div>

    </div>
  );
}

export default App;
