import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import styled from '../src/content.module.css';

function App() {
  let offset = 0;
  const [content, setContent] = useState([]);
  //const [loadingTrend, setLoadingTrend] = useState(false);


  const loadTrending = () => {
    //setLoadingTrend(true);
    axios.get(`http://localhost:3001/movies?limit=5&page=${offset}`)
      .then((res) => {
        //setLoadingTrend(false);
        var newContent = [];


        res.data.forEach((item, index) => newContent.push(item));
        setContent((current) => [...current, ...newContent]);
        //console.log(newContent)
        //setContent((current) => [...current, ...newContent]);
      })
      .catch((err) => { console.log(err) })
    offset += 5;
    console.log('offset: ' + offset)
  }

  const handleSubmit= (e) => {
    e.preventDefault();
    console.log(e.target[0].value);

    axios.post(`http://localhost:3001/feedback?err=${e.target[0].value}`)
    .then((res) => {
      console.log(res.data)
    })
    .catch((err) => console.log(err));
  }


  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor='feedback'>Pick one: </label>
        <select name='feedback'>
        <option value='mango'>Mango</option>
        <option value='banana'>Banana</option>
        <option value='pineapple'>pineapple</option>
        </select>
        <br/>
        <button type='submit' >Submit</button>
      </form>

    </div>
  );
}

export default App;
