import { useEffect } from 'react';
import './App.css';

function App() {
  let Div = document.getElementById('scrollDemo');

  const handleScroll = (e) => {
    let target = e.target;


    switch (target.id) {
      case 'btnScrollLeft':
        Div.scrollLeft += 100;
        //IMPORTANT! if x = 500, load new element
        console.log(Div.scrollLeft)


        break;

      case 'btnScrollRight':
        Div.scrollLeft -= 100;
        console.log(Div.scrollLeft)
        break;

    }

  }

  useEffect(() => {
    Div.addEventListener('scroll', function () {
      console.log('scroll')
    })
  })
  /*
  const scrollX = () => {
    document.getElementById('scrollDemo').scrollLeft += 50;
  }*/

  return (
    <div>
      <div id="scrollDemo">
        <div className='container'>
          <div className='box'>1</div>
          <div className='box'>2</div>
          <div className='box'>3</div>
          <div className='box'>4</div>
        </div>
      </div>

      <div onClick={handleScroll}>
        <button id="btnScrollLeft">Scroll X</button>
        <button id="btnScrollRight">Scroll invertX</button>

      </div>
    </div>
  );
}

export default App;
