import React, { useEffect, useState } from "react";
import jwt_decode from 'jwt-decode';
import { BrowserRouter as Router, Routes, Route, useNavigate, Navigate } from 'react-router-dom';


function Login() {
    const [user, setUser] = useState({});
    let navigate = useNavigate();

    function handleCallbackResponse(response) {
        console.log('id token:' + response.credential);
        var userObject = jwt_decode(response.credential);
        sessionStorage.setItem('Auth Token', response.refreshToken);
        console.log(userObject);
        setUser(userObject);
        document.getElementById('signInDiv').hidden = true;
        navigate('/home');
        //test

    }

    function handleSignOut(event) {
        setUser({});
        navigate('/login');
        document.getElementById('signInDiv').hidden = false;
    }

    useEffect(() => {
        /* global google */
        google.accounts.id.initialize({
            client_id: '53905920997-t8t7o0idb1s0ghs0mobpaccp509jrlcd.apps.googleusercontent.com',
            callback: handleCallbackResponse

        });

        google.accounts.id.renderButton(
            document.getElementById('signInDiv'),
            { theme: 'outline', size: 'large' }
        );

        //test
        if (window.location.pathname == '/') {
            navigate('/login');
        } else { console.log('unknow location') }

    }, []);

    let authToken = sessionStorage.getItem('Auth Token');

    if (!authToken) {
        navigate('/login')
    }

    return (
        <div>
            <div id='signInDiv'></div>
            {
                Object.keys(user).length != 0 && <button onClick={(e) => handleSignOut(e)}>Sign Out</button>
            }
        </div>
    );
}

export default Login;
