import axios from 'axios';
import { useState, useEffect } from 'react';
import './App.css';

function App() {
  let offset = 0;
  const [pokemon, setPokemon] = useState([])

  const loadPokemon = () => {
    axios.get(`https://pokeapi.co/api/v2/pokemon?limit=10&offset=${offset}`)
      .then((res) => {
        const newPokemon = [];
        res.data.results.forEach((p) => newPokemon.push(p.name));
        setPokemon((oldPokemon) => [...oldPokemon, ...newPokemon, '+++'])
        console.log(oldPokemon)
      })
      .catch((err) => console.log(err))
    offset += 10;
  }


  const handleScroll = (e) => {
    let target = e.target;
    let Div = document.getElementById('scrollDemo');

    switch (target.id) {
      case 'btnScrollLeft':
        Div.scrollLeft += 100;

        //IMPORTANT! if x = 500, load new element
        console.log('click ' + Div.scrollLeft)
        if (Div.scrollLeft === 500) { console.log('congrat! you reach 500') }
        break;

      case 'btnScrollRight':
        Div.scrollLeft -= 100;
        console.log('click ' + Div.scrollLeft)
        break;

    }
  }

  const getScrollPos = (e) => {
    //let Div = document.getElementById('scrollDemo');
    console.log('scroll ' + e.target.scrollLeft)
    if (e.target.scrollLeft >= 500) {
      console.log('congrat! you reach 500')
      loadPokemon();
    }
  }

  useEffect(() => {
    loadPokemon();
  }, [])

  /*
  const scrollX = () => {
    document.getElementById('scrollDemo').scrollLeft += 50;
  }*/

  return (
    <div>
      <div>
        {pokemon.map((p, i) => {
          return <p key={i}>{p}</p>
        })}
      </div>

      <div id="scrollDemo" onScroll={getScrollPos}>
        <div className='container'>
          {pokemon.map((p, i) => {
            return <div key={i} className='box'>{p}</div>
          })}
        </div>

      </div>

      <div onClick={handleScroll}>
        <button id="btnScrollLeft">Scroll X</button>
        <button id="btnScrollRight">Scroll invertX</button>

      </div>
    </div>
  );
}

export default App;
