import './css/alt.css';
import Head from 'next/head';
import { useEffect, useState } from 'react';
import axios from 'axios';



function App() {
  let offset = 0;
  const [pokemon, setPokemon] = useState([])

  const loadMorePokemon = () => {
    axios.get(`https://pokeapi.co/api/v2/pokemon?limit=10&offset=${offset}`)
    .then(({data}) => {

      const newPokemon = [];
      data.results.forEach((p) => newPokemon.push(p.name));
      setPokemon((oldPokemon) => [...oldPokemon, ...newPokemon ]) 
    })
    .catch((err) => { console.log(err) })
    offset += 10;
  }

  const handleScroll = (e) => {
    if (window.innerHeight + e.target.documentElement.scrollTop + 1 >= e.target.documentElement.scrollHeight) {
      loadMorePokemon();
    }
  }

  useEffect(()=>{
    loadMorePokemon();
    window.addEventListener('scroll', handleScroll)
  },[])

  return (
    <div>
      <Head>
        <title>infinite scroll</title>
      </Head>

      <main>
        <div style={{width:'50px', height:'50px', border:'1px solid #000'}}>
          {pokemon.map((p, i) => {
            return <div key={i}>{p}</div>;
          })}
        </div>
      </main>
    </div>
  );
}

export default App;
