import axios from 'axios';
import { useState, useEffect } from 'react';
import './App.css';

function App() {
  let offset = 0;
  const [pokemon, setPokemon] = useState([])

  const loadPokemon = () => {

    axios.get(`https://pokeapi.co/api/v2/pokemon?limit=4&offset=${offset}`)
      .then((res) => {
        var newPokemon = [];
        var scrollX, scrollInvertX;
        scrollX = document.getElementById('scrollX')
        scrollInvertX = document.getElementById('scrollInvertX')

        if (offset <= 12) {
          let loading = '++';
          res.data.results.forEach((p) => newPokemon.push(p.name));
          console.log(newPokemon)
          setPokemon((current) => [...current, ...newPokemon, loading.replace('++', ...newPokemon)])

        }
        if (offset > 12) {
          console.log('you reach the end.')

        }

      })
      .catch((err) => console.log(err))
    offset += 4;

  }


  const handleScrollX = (e) => {
    const Div = document.getElementById('scrollDemo');
    var container = document.getElementById('container');
    var px = (75 * (container.clientWidth)) / 100;
    Div.scrollLeft += px;
    loadPokemon();
    console.log('position: ' + Div.scrollLeft)
  }
  const handleScrollInvertX = (e) => {
    const Div = document.getElementById('scrollDemo');
    var container = document.getElementById('container');
    var px = (90 * (container.clientWidth)) / 100;
    Div.scrollLeft -= px;
    console.log('position: ' + Div.scrollLeft)
  }


  const getScrollPos = (e) => {
    //let Div = document.getElementById('scrollDemo');
    //console.log('scroll ' + e.target.scrollLeft)
    if (e.target.scrollLeft >= 500) {
      //console.log('congrat! you reach 500')

    }
  }

  useEffect(() => {
    loadPokemon();
    var scrollX, scrollInvertX;

    scrollX = document.getElementById('scrollX')
    scrollInvertX = document.getElementById('scrollInvertX')


    scrollX.addEventListener('click', handleScrollX)
    scrollInvertX.addEventListener('click', handleScrollInvertX)

  }, [])

  /*
  const scrollX = () => {
    document.getElementById('scrollDemo').scrollLeft += 50;
  }*/

  return (
    <div id="container">

      <div id="scrollDemo" onScroll={getScrollPos}>
        {/*<div className='container'>
          {pokemon.map((p, i) => {
            return <div key={i} className='box'>{p}</div>
          })}
        </div>*/}
        <div className='container'>
          <div className='box'>{pokemon}</div>
        </div>
      </div>

      <div >
        <button type="button" id="scrollX">Scroll X</button>
        <button type="button" id="scrollInvertX">Scroll invertX</button>
      </div>

    </div>
  );
}

export default App;
