import axios from 'axios';
import { useState, useEffect } from 'react';
import './App.css';

function App() {
  let offset = 0;
  const [pokemon, setPokemon] = useState([]);
  const [loading, setLoading] = useState(false);
  const [output, setOutput] = useState([]);

  const loadPokemon = () => {
    setLoading(true);
    axios.get(`http://localhost:3001/trending`)
      .then((res) => {
        //var newPokemon = [];
        //setLoading(false);
        console.log(res.title)
        /*
        if (offset <= 12) {
          res.data.results.forEach((p) => newPokemon.push(p.title, p.img));
          console.log(newPokemon)
          setPokemon((current) => [...current, ...newPokemon]);
        }
        if (offset > 13) {
          console.log('you reach the end.')
        }
        */
      })
      .catch((err) => console.log(err))
    offset += 4;
    console.log('offset: ' + offset)

  }


  const handleScrollX = (e) => {
    const Div = document.getElementById('scrollDemo');
    var container = document.getElementById('container');
    var px = (75 * (container.clientWidth)) / 100;
    Div.scrollLeft += px;
    loadPokemon();
    console.log('position: ' + Div.scrollLeft)
  }
  const handleScrollInvertX = (e) => {
    const Div = document.getElementById('scrollDemo');
    var container = document.getElementById('container');
    var px = (75 * (container.clientWidth)) / 100;
    Div.scrollLeft -= px;
    console.log('position: ' + Div.scrollLeft)
  }

  /*
  const scrollX = () => {
    document.getElementById('scrollDemo').scrollLeft += 50;
  }*/


  useEffect(() => {
    loadPokemon();
    var scrollX, scrollInvertX;
    var scrollDemo = document.getElementById('scrollDemo')
    scrollX = document.getElementById('scrollX')
    scrollInvertX = document.getElementById('scrollInvertX')

    scrollX.addEventListener('click', handleScrollX)
    scrollInvertX.addEventListener('click', handleScrollInvertX)
    scrollDemo.addEventListener('touchend', loadPokemon)
    //window.addEventListener('scroll', getScrollPos)

  }, [])


  return (

    <div id="container">
      <div id="scrollDemo">
        {/*<div className='container'>
          <div name='box_top' style={{ border: '1px solid red' }}>
            {pokemon.map((e, i) => {
              if (i % 2 === 0) {
                return <div key={i} className='box'><p>{e}</p>{loading && (<p>Loading..</p>)}</div>
              }
            })}
          </div>
          <div name='box_bottom' style={{ border: '1px solid blue' }}>
            {pokemon.map((e, i) => {
              if (i % 2 === 1) {
                return <div key={i} className='box'><img src={e} /></div>
              }

            })}
          </div>
        </div>*/}
      </div>

      <div>
        <button type="button" id="scrollX">Scroll X</button>
        <button type="button" id="scrollInvertX">Scroll invertX</button>
      </div>

    </div>
  );
}

export default App;
