import './css/alt.css';

import { useEffect } from 'react';



function App() {

  const add_hyphen = (e) => {
    var str = e.target.value;

    str.replace('-', '');
    if (str.length > 3) {
      str = str.substring(0,3) + '-' + str.substring(3)
    }
  }

  return (
    <div>
      <form>
        <input type='text' onChange={(e) => {add_hyphen(e)}}/>
      </form>

    </div>
  );
}

export default App;
