import axios from 'axios';
import { useState, useEffect } from 'react';
import './App.css';

function App() {
  let offset = 0;
  const [pokemon, setPokemon] = useState([])

  const loadPokemon = () => {

      axios.get(`https://pokeapi.co/api/v2/pokemon?limit=10&offset=${offset}`)
      .then((res) => {
        const newPokemon = [];
        res.data.results.forEach((p) => newPokemon.push(p.name));
        console.log(newPokemon)
        setPokemon((oldPokemon) => [...oldPokemon, ...newPokemon])
      })
      .catch((err) => console.log(err))
    offset += 10;
  }


  return (
    <div>
      <button type='button' onClick={loadPokemon}>Load more</button>


    </div>
  );
}

export default App;
