import './App.css';

function App() {

  let div = document.getElementById('scrollDemo');
  div.style.overflow = 'hidden';

  const scrollX = () => {
    div.scrollLeft += 50;
  }

  return (
    <div>
      <div id="scrollDemo">
        <div className='container'>
          <div className='box'>1</div>
          <div className='box'>2</div>
          <div className='box'>3</div>
          <div className='box'>4</div>
        </div>
      </div>

      <div id="control">
        <button onClick={(e) => scrollX}>Scroll X</button>
        <button id="btnScrollRight">Scroll invertX</button>

      </div>
    </div>
  );
}

export default App;
